import {
  deleteProject,
  getProjectList,
  exportProjectToExcel,
} from '@/services/projects';
import { PageContainer, ProTable } from '@ant-design/pro-components';
import { Link } from '@umijs/max';
import { Button, Dropdown, Popconfirm, Tag, Tooltip, App } from 'antd';
import { useRef } from 'react';

const HistoryPage = () => {
  const { message } = App.useApp();
  const actionRef = useRef();
  const columns = [
    {
      title: '项目名称',
      dataIndex: 'name',
      key: 'name',
      render: (text, record) => (
        <Link to={`/assessment/detail/${record.id}`}>{text}</Link>
      ),
    },
    {
      title: '是否模板',
      dataIndex: 'is_template',
      key: 'is_template',
      width: 100,
      align: 'center',
      render: (value) =>
        value === 1 || value === true ? (
          <Tag color="blue">当前模板</Tag>
        ) : (
          '-'
        ),
    },
    {
      title: '报价总计 (万元)',
      dataIndex: 'final_total_cost',
      key: 'final_total_cost',
      width: 140,
      valueType: 'money',
      sorter: true,
      align: 'right',
    },
    {
      title: '风险总分',
      dataIndex: 'final_risk_score',
      key: 'final_risk_score',
      sorter: true,
      align: 'right',
      width: 140,
    },
    {
      title: '创建时间',
      dataIndex: 'created_at',
      key: 'created_at',
      valueType: 'dateTime',
      sorter: true,
      align: 'center',
      width: 200,
    },
    {
      title: '操作',
      valueType: 'option',
      key: 'option',
      align: 'center',
      width: 260,
      render: (text, record, _, action) => [
        <Link key="view" to={`/assessment/detail/${record.id}`}>
          查看
        </Link>,
        <Link key="reassess" to={`/assessment/new?template_id=${record.id}`}>
          重新评估
        </Link>,
        <Dropdown
          key="export"
          menu={{
            items: [
              {
                key: 'internal',
                label: (
                  <a
                    href={exportProjectToExcel(record.id)}
                    target="_blank"
                    rel="noreferrer"
                  >
                    导出Excel（内部）
                  </a>
                ),
              },
              {
                key: 'external',
                label: (
                  <a
                    href={`${exportProjectToExcel(record.id)}?version=external`}
                    target="_blank"
                    rel="noreferrer"
                  >
                    导出Excel（对外）
                  </a>
                ),
              },
            ],
          }}
        >
          <Button type="link">导出</Button>
        </Dropdown>,
        (record.is_template === 1 || record.is_template === true) ? (
          <Tooltip
            key="delete-disabled"
            title="当前模板不可删除，请先在新评估中设置新的模板"
          >
            <Button type="link" disabled>
              删除
            </Button>
          </Tooltip>
        ) : (
          <Popconfirm
            key="delete"
            title="确认删除此项目？"
            onConfirm={async () => {
              await deleteProject(record.id);
              message.success('项目已删除');
              actionRef.current?.reload();
            }}
          >
            <a>删除</a>
          </Popconfirm>
        ),
      ],
    },
  ];

  return (
    <PageContainer>
      <ProTable
        columns={columns}
        actionRef={actionRef}
        cardBordered
        request={async (params) => {
          const res = await getProjectList();
          return {
            data: res.data,
            success: true,
          };
        }}
        rowKey="id"
        search={{
          labelWidth: 'auto',
        }}
        pagination={{
          pageSize: 10,
        }}
        dateFormatter="string"
        headerTitle="历史项目列表"
      />
    </PageContainer>
  );
};

export default HistoryPage;

