      actionRef={actionRef}
      cardBordered
      request={async () => {
        const res = await getTravelCostList();
        return {
          data: res.data,
          success: true,
        };
      }}
      editable={{
        type: 'single',
        onSave: async (rowKey, data) => {
          try {
            // 确保 cost_per_month 是数字类型
            const payload = {
              item_name: data.item_name,
              cost_per_month: Number(data.cost_per_month),
            };

            if (data.id && data.id < 1000000000000) {
              // 更新已有记录
              await updateTravelCost(data.id, payload);
              message.success('更新成功');
            } else {
              // 新建记录
              await createTravelCost(payload);
              message.success('创建成功');
            }
            // 刷新表格数据
            actionRef.current?.reload();
            return true;
          } catch (error) {
            message.error(`操作失败: ${error.message}`);
            console.error('保存差旅成本失败:', error);
            return false;
          }
        },
      }}
      rowKey="id"
      search={false}
      options={false}
      pagination={false}
      dateFormatter="string"
      headerTitle="差旅成本管理"
      toolBarRender={() => [
        <Button
          key="button"
          type="primary"
          onClick={() => {
            actionRef.current?.addEditRecord?.({
              id: Date.now(), // 临时唯一key
              item_name: '',
              cost_per_month: 0,
            });
          }}
        >
          新建
        </Button>,
      ]}
    />
  );
};

const ConfigPage = () => {
